from flexdown.modules._41aac2869ce5c38667aebd35627926af8169b3c1e6e93fa0aa50e73ff1dbfc9f import *

class Counter(rx.ComponentState):
    count: int = 0

    @rx.event
    def increment(self):
        self.count += 1

    @rx.event
    def decrement(self):
        self.count -= 1

    @classmethod
    def get_component(cls, **props) -> rx.Component:
        return rx.hstack(
            rx.button("Decrement", on_click=cls.decrement),
            rx.heading(cls.count),
            rx.button("Increment", on_click=cls.increment),
        )

counter = Counter.create
