from flexdown.modules._2aa8d62b442f08310d1bdb1ec9734ef0372c969d22e1213d94f1cb8b4984a1e1 import *

import reflex as rx
from reflex_image_zoom import image_zoom

def zoom():
    src = "/blog/custom_components.jpeg"
    return rx.hstack(
        rx.vstack(
            rx.text("Original Image"),
            rx.image(src=src),
        ),
        rx.vstack(
            rx.text("Click to Zoom Image"),
            image_zoom(
                rx.image(src=src),
                width="50%",
            )
        ),
    )
