ICONS = {
    # Socials
    "github": github,
    "discord": discord,
    "twitter": twitter,
    # Theme
    "sun": sun,
    "moon": moon,
    "arrow_right": arrow_right,
    "copy": copy,
    "arrow_down": arrow_down,
    "arrow_down_big": arrow_down_big,
    "new_tab": new_tab,
    "eye": eye,
    "select": select,
    "history": history,
    "clipboard": clipboard,
}

LiteralIcon = Literal[
    "github",
    "discord",
    "twitter",
    "sun",
    "moon",
    "arrow_right",
    "copy",
    "arrow_down",
    "arrow_down_big",
    "new_tab",
    "eye",
    "select",
    "history",
    "clipboard",
]


def get_icon(icon: LiteralIcon, **props) -> rx.Component:
    if icon not in ICONS:
        raise ValueError(
            f"Icon '{icon}' not found. Available icons: {list(ICONS.keys())}",
        )
    return rx.html(
        ICONS[icon],
        display="flex",
        justify_content="center",
        align_items="center",
        **props,
    )
